import { useState } from 'react';
import './NavBarStyle.css';

function NavBar(){

    let items = [
        {
            id:1,
            value:'Home',
            href:"Home.html"
        },
        {
            id:2,
            value:'About',
            href:'About.html'
        },
        {
            id:3,
            value:'Timeline',
            href:'Timeline.html'
        },
        {
            id:4,
            value:'Contact',
            href:'Contact.html'
        }
    ];

    let [currentIndex,setCurrentIndex] = useState(0);

    return(
        <div>
            <nav>
                <div className="logo">
                    Portfolio
                </div>
                <input type="checkbox" id="click"/>
                <label for="click" className="menu-button">
                    <i className="fas fa-bars"></i>
                </label>
                <ul>
                    {
                    items.map(
                        (item,index) =>
                        <li key={item.id}>
                            <a href={item.href} onClick={(e) => {e.preventDefault(); setCurrentIndex(index)}} className={index===currentIndex?"active":""}>
                                {item.value}
                            </a>
                        </li>
                        )
                    }
                </ul>
            </nav>
             <footer class="sticky-footer">
             <div class="media-icons">
                 <a href="https://github.com/Dhanush-Karthik"><i classNa="fab fa-github"></i></a>
                 <a href="https://www.linkedin.com/in/dhanush-karthik-348a741bb"><i class="fab fa-linkedin"></i></a>
                 <a href="https://www.facebook.com/profile.php?id=100006168917057"><i class="fab fa-facebook"></i></a>
                 <a href="https://twitter.com/Dhanushdk45"><i class="fab fa-twitter"></i></a>
                 <a href="https://www.instagram.com/_._dk7_._/"><i class="fab fa-instagram"></i></a>
             </div>
                 </footer>
        </div>
    );
}

export default NavBar;
