import { useState } from 'react';
import './NavBarStyle.css';
import Logo from '/public';

function NavBar(){

    let items = [
        {
            id:1,
            value:'Home',
            href:"Home.html"
        },
        {
            id:2,
            value:'About',
            href:'About.html'
        },
        {
            id:3,
            value:'Timeline',
            href:'Timeline.html'
        },
        {
            id:4,
            value:'Contact',
            href:'Contact.html'
        }
    ];

    let [currentIndex,setCurrentIndex] = useState(0);

    return(
        <div>
            <nav>
                <div className="logo">
                    Portfolio
                </div>
                <input type="checkbox" id="click"/>
                <label htmlFor="click" className="menu-button">
                    <i className="fas fa-bars"></i>
                </label>
                <ul>
                    {
                    items.map(
                        (item,index) =>
                        <li key={item.id}>
                            <a href={item.href} onClick={(e) => {e.preventDefault(); setCurrentIndex(index)}} className={index===currentIndex?"active":""}>
                                {item.value}
                            </a>
                        </li>
                        )
                    }
                </ul>
            </nav>
            <div className="content">
        <img src='..public/profile.jpg' alt="profile"/>
        <div className="info">
            <h2>Dhanush Karthik K S</h2><h4>Entry level software engineer </h4>
            <p>Entry-level software engineer, predominantly working with Java and a self-taught Spring boot developer. Eager to join an enterprise, where I
                can contribute with my technical skillset.</p>
            <a href="about.html" className="info-btn">More info</a>
        </div>
    </div>
             <footer className="sticky-footer">
             <div className="media-icons">
                 <a href="https://github.com/Dhanush-Karthik"><i className="fab fa-github"></i></a>
                 <a href="https://www.linkedin.com/in/dhanush-karthik-348a741bb"><i className="fab fa-linkedin"></i></a>
                 <a href="https://www.facebook.com/profile.php?id=100006168917057"><i className="fab fa-facebook"></i></a>
                 <a href="https://twitter.com/Dhanushdk45"><i className="fab fa-twitter"></i></a>
                 <a href="https://www.instagram.com/_._dk7_._/"><i className="fab fa-instagram"></i></a>
             </div>
                 </footer>
        </div>
    );
}

export default NavBar;
