import { useState } from "react";
import "./NavBarStyle.css";
import { Link } from "react-router-dom";

function NavBar() {
  let items = [
    {
      id: 1,
      value: "Home",
      href: "/",
    },
    {
      id: 2,
      value: "About",
      href: "/about",
    },
    {
      id: 3,
      value: "Timeline",
      href: "/timeline",
    },
    {
      id: 4,
      value: "Contact",
      href: "/contact",
    },
  ];

  let [currentIndex, setCurrentIndex] = useState(0);

  return (
    <div>
      <nav>
        <div className="logo">
          <Link
            to="/"
            onClick={(e) => {
              console.log(e.target);
            }}
          >
            Portfolio
          </Link>
        </div>
        <input type="checkbox" id="click" />
        <label htmlFor="click" className="menu-button">
          <i className="fas fa-bars"></i>
        </label>
        <ul>
          {/* {items.map((item, index) => (
            <li key={item.id}>
              <Link
                to={item.href}
                onClick={(e) => {
                  e.preventDefault();
                  setCurrentIndex(index);
                }}
                className={index === currentIndex ? "active" : ""}
              >
                {item.value}
              </Link>
            </li>
          ))} */}
          <li>
            <Link
            key={2}
              to={"/about"}
              onClick={(e) => {
                e.preventDefault();
                setCurrentIndex(2);
              }}
              className={1 === currentIndex ? "active" : ""}
            >
              About
            </Link>
          </li>
          <li>
            <Link
            key={3}
              to={"/a"}
              onClick={(e) => {
                e.preventDefault();
                setCurrentIndex(3);
              }}
              className={1 === currentIndex ? "active" : ""}
            >
              TimeLine
            </Link>
          </li>
          <li>
            <Link
            key={3}
              to={"/about"}
              onClick={(e) => {
                e.preventDefault();
                setCurrentIndex(1);
              }}
              className={1 === currentIndex ? "active" : ""}
            >
              About
            </Link>
          </li>
          <li>
            <Link
            key={1}
              to={"/about"}
              onClick={(e) => {
                e.preventDefault();
                setCurrentIndex(1);
              }}
              className={1 === currentIndex ? "active" : ""}
            >
              About
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  );
}

function CustomLink({ to, children, ...props }) {
  // const path = window.location.pathname;

  return (
    <li>
      <Link to={to} {...props}>
        {children}
      </Link>
    </li>
  );
}

export default NavBar;
