import { useState } from 'react';
import './NavBarStyle.css';

function NavBar(){

    let items = [
        {
            id:1,
            value:'Home',
            href:"/"
        },
        {
            id:2,
            value:'About',
            href:"/about"
        },
        {
            id:3,
            value:'Timeline',
            href:"/timeline"
        },
        {
            id:4,
            value:'Contact',
            href:"/contact"
        }
    ];

    let [currentIndex,setCurrentIndex] = useState(0);

    return(
        <div>
            <nav>
                <div className="logo">
                    Portfolio
                </div>
                <input type="checkbox" id="click"/>
                <label htmlFor="click" className="menu-button">
                    <i className="fas fa-bars"></i>
                </label>
                <ul>
                    {
                    items.map(
                        (item,index) =>
                        <li key={item.id}>
                            <a href={item.href} onClick={(e) => {e.preventDefault(); setCurrentIndex(index)}} className={index===currentIndex?"active":""}>
                            <Link to="/">
                                    {item.value}
                            </Link>
                            </a>
                        </li>
                        )
                    }
                </ul>
            </nav>
        </div>
    );
}

export default NavBar;
