import { useState } from 'react';
import './NavBarStyle.css';
import { Link } from 'react-router-dom';
// import {Link} from "react-router-dom";

function NavBar(){

    let items = [
        {
            id:1,
            value:'Home',
            href:"/"
        },
        {
            id:2,
            value:'About',
            href:"/about"
        },
        {
            id:3,
            value:'Timeline',
            href:"/timeline"
        },
        {
            id:4,
            value:'Contact',
            href:"/contact"
        }
    ];

    let [currentIndex,setCurrentIndex] = useState(0);

    return(
        <div>
            <nav>
                <div className="logo">
                    Portfolio
                </div>
                <input type="checkbox" id="click"/>
                <label htmlFor="click" className="menu-button">
                    <i className="fas fa-bars"></i>
                </label>
                <ul>
                    {
                    items.map(
                        (item,index) =>
                        <li key={item.id}>
                            <CustomLink to={item.href} className="nav-e" onClick={(e) => {e.preventDefault(); setCurrentIndex(index)}} className={index===currentIndex?"active":""}>
                                {item.value}
                            </CustomLink>
                        </li>
                        )
                    }
                </ul>
            </nav>
        </div>
    );
}

function CustomLink({to,children,...props}){
    const path = window.location.pathname;

    return (
        <li className={path === to ? "active" : ""}>
            <Link to={to} {...props}>{children}</Link>
        </li>
    );
}

export default NavBar;
