import './FootBarStyle.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

//npm install --save @fortawesome/free-brands-svg-icons
import {faGithub,faInstagram,faFacebook,faTwitter,faLinkedin} from '@fortawesome/free-brands-svg-icons'

function FootBar(){

    let items = [
        {
            id: 0,
            href: "",
            class:faGithub
        },
        {
            id: 1,
            href: "",
            class:faLinkedin
        },
        {
            id: 2,
            href: "",
            class:faFacebook
        },
        {
            id: 3,
            href: "",
            class:faTwitter
        },
        {
            id: 4,
            href: "",
            class:faInstagram
        }
    ]
    return(
        <div className='footer'>
            <div className="media-icons">
                        items.map(item)
                         <a href="https://github.com/Dhanush-Karthik"><FontAwesomeIcon icon={faGithub} style={{color: "#ffffff",}} /></a>
                         <a href="https://www.linkedin.com/in/dhanush-karthik-348a741bb"><FontAwesomeIcon icon={faLinkedin} style={{color: "#ffffff",}} /></a>
                         <a href="https://www.facebook.com/profile.php?id=100006168917057"><FontAwesomeIcon icon={faFacebook} style={{color: "#ffffff",}} /></a>
                         <a href="https://twitter.com/Dhanushdk45"><FontAwesomeIcon icon={faTwitter} style={{color: "#ffffff",}} /></a>
                         <a href="https://www.instagram.com/_._dk7_._/"><FontAwesomeIcon icon={faInstagram} style={{color: "#ffffff",}} /></a>
                </div>
        </div>
    );
}

export default FootBar;
