import './NavBarStyle.css';

function NavBar(){

    let items = [
        {
            id:1,
            value:'Home',
            href:"Home.html"
        },
        {
            id:2,
            value:'About',
            href:'About.html'
        },
        {
            id:3,
            value:'Timeline',
            href:'Timeline.html'
        },
        {
            id:4,
            value:'Contact',
            href:'Contact.html'
        }
    ];

    let [currentIndex,setCurrentIndex] = useState
    return(
        <nav>
            <div className="logo">
                Portfolio
            </div>
            <input type="checkbox" id="click"/>
            <label for="click" className="menu-button">
                <i className="fas fa-bars"></i>
            </label>
            <ul>
                {
                items.map(
                    (item,index) => 
                    <li key={index}>
                        <a href={item.href}>
                            {item.value}
                        </a>
                    </li>
                    )
                }
                {/* <li onClick={handleChange} id='1'>
                    <a href="index.html" onClick={handleChange}  >Home</a>
                </li>
                <li onClick={handleChange} id='2'>
                    <a href="about.html" onClick={handleChange} >About</a>
                </li>
                <li  id='3'>
                    <a href="timeline.html" onClick={handleChange} >Timeline</a>
                </li>
                <li onClick={handleChange} id='4'>
                    <a href="contact.html" onClick={handleChange} >Contact</a>
                </li> */}
            </ul>
        </nav>
    );
}

export default NavBar;
