import { useState } from 'react';
import './NavBarStyle.css';

function NavBar(){

    let list = ['Home','About']

    let handleChange = (e) => {
        e.preventDefault();
        setCurrentId(e.currentTarget.id);
        setClassActive("active");
    }

    return(
        <nav>
            <div className="logo">
                Portfolio
            </div>
            <input type="checkbox" id="click"/>
            <label for="click" className="menu-button">
                <i className="fas fa-bars"></i>
            </label>
            <ul>
                <li onClick={handleChange} id='1'>
                    <a href="index.html" onClick={handleChange} className={currentId===1?classActive:""} >Home</a>
                </li>
                <li onClick={handleChange} id='2'>
                    <a href="about.html" onClick={handleChange} className={currentId===2?classActive:""}>About</a>
                </li>
                <li  id='3'>
                    <a href="timeline.html" onClick={handleChange} className={currentId===3?classActive:""}>Timeline</a>
                </li>
                <li onClick={handleChange} id='4'>
                    <a href="contact.html" onClick={handleChange} className={currentId===4?classActive:""}>Contact</a>
                </li>
            </ul>
        </nav>
    );
}

export default NavBar;
